def add_numers(a,b):
    """
    Suma dos números y devuelve el resultado.

    Parameters:
    a (int): El primer número a sumar.
    b (int): El segundo número a sumar.

    Returns:
    int: La suma de los dos números.

    Example:
    >>> add_numers(2, 3)
    5
    """
    return a + b

def subtract_numbers(a,b):
    """
    Resta dos números y devuelve el resultado.

    Parameters:
    a (int): El primer número a restar.
    b (int): El segundo número a restar.

    Returns:
    int: La resta de los dos números.

    Example:
    >>> subtract_numbers(3, 2)
    1
    """
    return a - b

def split_numbers(a,b):
    """
    Divide dos números y devuelve el resultado.

    Parameters:
    a (int): El primer número a dividir.
    b (int): El segundo número a dividir.

    Returns:
    int: La divicion de los dos números.

    Example:
    >>> split_numbers(4, 2)
    2
    """
    return a / b 

def multiply_numbers(a,b):
    """
    Multiplica dos números y devuelve el resultado.

    Parameters:
    a (int): El primer número a multiplicar.
    b (int): El segundo número a multiplicar.

    Returns:
    int: La multiplicacion de los dos números.

    Example:
    >>> multiply_numbers(3, 2)
    6
    """
    return a * b